<?phpclass Inventory_DatabaseBackup extends Inventory_InventoryBase{    public $Show_Query                      = false;            // (false) TRUE = display database queries    public $Module_Offline                  = false;            // (false) TRUE = display offline message to users           public function  __construct()    {        parent::__construct();        $this->SetSQLInventory();   // set the database connection to the inventory database        		$this->Classname = get_class($this);        $this->ClassInfo = array(            'Created By'    => 'Richard Witherspoon',            'Created Date'  => '2013-08-09',            'Updated By'    => '',            'Updated Date'  => '',            'Filename'      => $this->Classname,            'Version'       => '1.0',            'Description'   => 'Backup the given database',            'Update Log'    => array(                '2013-08-09_1.0'    => "Module Created",            ),        );            } // -------------- END __construct --------------        public function Execute()    {        # FUNCTION :: Main function called after instantiating this class                //$a = $this->GetSetting('db_database');        //$b = $this->GetSetting('db_tables');                //$this->EchoVar('', $a);        //$this->EchoVar('', $b);                $Obj_DB                     = new Utilities_DatabaseBackup();        $Obj_DB->Database_Name      = $this->GetSetting('db_database');     // get setting from inventory_settings table        $Obj_DB->Database_Tables    = $this->GetSetting('db_tables');       // get setting from inventory_settings table        $Obj_DB->Execute();    }            }  // -------------- END CLASS --------------