<?phpclass Inventory_InventoryProducts extends Inventory_InventoryBase{    public $Show_Query                  = false;    public $Group                       = '';    public $Table_Inventory_Products    = 'inventory_products';    public $Value_Instock               = 'IN STOCK';    public $Value_Backorder             = 'BACKORDER';        public $Barcode                     = '';    public $Retailer_Code               = '';    public $Add_Record_Link             = '';        public $Inventory_Assemblies_Id     = 0;        // will hold value    	public $Group_1_Select = array('Monitor', 'Access Point', 'Dock', 'SyncBox', 'Battery', 'Strap', 'Accessory', 'Cable', 'Other');	public $Group_2_Select = array('Complete', 'Assembly', 'Raw Part', 'Bad', 'Development', 'Other');	    public function  __construct()    {        parent::__construct();                $this->Classname = get_class($this);        $this->ClassInfo = array(            'Created By'    => 'Richard Witherspoon',            'Created Date'  => '2012-11-26',            'Updated By'    => 'Richard Witherspoon',            'Updated Date'  => '2013-03-18',            'Filename'      => $this->Classname,            'Version'       => '1.3.3',            'Description'   => 'Allows editing inventory',            'Update Log'    => array(                '2012-11-29_1.1'    => "Added AddScriptGetBarcode() and functionality for getting next unique barcode",                '2013-01-02_1.2'    => "Added show/hide toggle functionality to form divs",                '2013-02-26_1.3'    => "Modified to support new database fields",                '2013-03-02_1.3.1'  => "Added Default_List_Size variable and set to 1,000 records. Modified print_barcode stuff",				'2013-03-11_1.3.2'  => "Added field for 'group_1' and 'group_2' - new field in database",                '2013-03-18_1.3.3'  => "Javascript to allow TAB to select the first auto-complete option",            ),        );                                $this->SetParameters(func_get_args());        $this->Barcode          = $this->GetParameter(0);        $this->Add_Record_Link  = HexDecodeString($this->GetParameter(1));        $this->Retailer_Code    = $this->GetParameter(2);                        $this->Table            = 'inventory_products';        $this->Add_Submit_Name  = 'INVENTORY_PRODUCTS_SUBMIT_ADD';        $this->Edit_Submit_Name = 'INVENTORY_PRODUCTS_SUBMIT_EDIT';        $this->Index_Name       = 'inventory_products_id';        $this->Flash_Field      = 'inventory_products_id';        $this->Default_Where    = '';  // additional search conditions        $this->Default_Sort     = 'barcode';  // field for default table sort                $this->Field_Titles = array(            'inventory_products.inventory_products_id'                  => 'Inventory Products Id',            'inventory_products.description'                            => 'Description',            'inventory_products.reorder_point'                          => 'Reorder Point',            'inventory_products.barcode'                                => 'Barcode',            'inventory_products.manufacturer_code'                      => 'Manufacturer Code',            'inventory_products.retailer_code'                          => 'Retailer Code',            'inventory_products.status_use_assembly_pricing'            => 'Status Use Assembly Pricing',            'inventory_products.status_hide_salesorder'                 => 'Status Hide From Salesorder',            'inventory_products.status_backorder'                       => 'Status Backorder',            'inventory_products.date_last_checked'                      => 'Last Checked Date',                        'inventory_products.active AS COGS'                         => 'COGS - Total Inventory',            'inventory_products.active AS SINGLE_COGS'                  => 'COGS - Single Item',                        '(inventory_products.part_cost *                 (SELECT (SUM(`qty_in`) - SUM(`qty_out`)) AS INVENTORY_PHYSICAL     FROM `inventory_counts`     WHERE `inventory_counts`.`barcode`=`inventory_products`.`barcode` AND `inventory_counts`.active=1)                ) AS QUICK_VALUE' => 'Quick Value',                                                'inventory_products.serialized'                             => 'Serialized',            'inventory_products.leadtime_days'                          => 'Leadtime (days)',            'inventory_products.part_cost'                              => 'Part Cost (default)',            'inventory_products.vendor'                                 => 'Vendor',            'inventory_products.notes'                                  => 'Notes',            						'inventory_products.group_1'                      => 'Group 1',			'inventory_products.group_2'                      => 'Group 2',			                        'inventory_products.status_retired'                         => 'Retired',            'inventory_products.status_allow_zerocost'                  => 'Allow Zero Cost',                        'inventory_products.price_reference_source'                 => 'Reference Source',            'inventory_products.price_reference_number'                 => 'Reference Number',            'inventory_products.price_reference_date'                   => 'Reference Date',            'inventory_products.price_reference_url'                    => 'Reference URL',            'inventory_products.price_reference_price'                  => 'Reference Price',            'inventory_products.price_reference_quantity'               => 'Reference Quantity',            'inventory_products.price_reference_notes'                  => 'Reference Notes',                                    'inventory_products.type_class'                             => 'Type Class (A/B/C)',            'inventory_products.eoq_annual_usage'                       => 'Annual Usage',            'inventory_products.eoq_holding_cost'                       => 'Holding Cost',            'inventory_products.eoq_order_cost'                         => 'Order Cost',            'inventory_products.eoq_quantity_price_breaks'              => 'Quantity Price Breaks',                                    'inventory_products.active'                                 => 'Active',            'inventory_products.updated'                                => 'Updated',            'inventory_products.created'                                => 'Created',                                                                        "(SELECT SUM(`qty_in`) AS INV_CNT_IN                                FROM `inventory_counts`     WHERE `inventory_counts`.`barcode`=`inventory_products`.`barcode` AND `inventory_counts`.active=1) AS INV_CNT_IN"               => 'Inventory IN',            "(SELECT SUM(`qty_out`) AS INV_CNT_OUT                              FROM `inventory_counts`     WHERE `inventory_counts`.`barcode`=`inventory_products`.`barcode` AND `inventory_counts`.active=1) AS INV_CNT_OUT"              => 'Inventory OUT',            "(SELECT (SUM(`qty_hold`) - SUM(`qty_release`)) AS INV_CNT_HOLD     FROM `inventory_holds`      WHERE `inventory_holds`.`barcode`=`inventory_products`.`barcode` AND `inventory_holds`.active=1) AS INV_CNT_HOLD"               => 'Inventory HOLD',            "(SELECT (SUM(`qty_in`) - SUM(`qty_out`)) AS INVENTORY_PHYSICAL     FROM `inventory_counts`     WHERE `inventory_counts`.`barcode`=`inventory_products`.`barcode` AND `inventory_counts`.active=1) AS INVENTORY_PHYSICAL"       => 'Inventory Physical (In-Out+-Adjustment)',                        'inventory_products.active AS PRINT_LABEL'                  => 'Print Label',             "(                (SELECT SUM(`inventory_counts`.`qty_in`) - SUM(`inventory_counts`.`qty_out`) AS INVENTORY_TRUE_TMP_1 FROM `inventory_counts` WHERE `inventory_counts`.`barcode`=`inventory_products`.`barcode` AND `inventory_counts`.active=1) -                 (SELECT SUM(`qty_hold`) - SUM(`qty_release`) AS INVENTORY_ON_HOLD FROM `inventory_holds` WHERE `inventory_holds`.`barcode`=`inventory_products`.`barcode` AND `inventory_holds`.active=1)             ) AS INVENTORY_ON_SITE" => 'Inventory Available (Physical-Hold)',                               );                /*            "(SELECT SUM(`qty_hold`) AS INV_HOLD_IN FROM `inventory_holds`      WHERE `inventory_holds`.`barcode`=`inventory_products`.`barcode` AND `inventory_holds`.active=1) AS INV_HOLD_IN"                                    => 'Inventory On Hold',            "(SELECT SUM(`qty_release`) AS INV_HOLD_OUT FROM `inventory_holds`  WHERE `inventory_holds`.`barcode`=`inventory_products`.`barcode` AND `inventory_holds`.active=1) AS INV_HOLD_OUT"                                   => 'Inventory Hold Released',                        "(SELECT SUM(`qty_order`) AS INV_ORDER_IN FROM `inventory_orders`           WHERE `inventory_orders`.`barcode`=`inventory_products`.`barcode` AND `inventory_orders`.active=1) AS INV_ORDER_IN"                         => 'Inventory On Order',            "(SELECT SUM(`qty_cancel`) AS INV_ORDER_OUT FROM `inventory_orders`         WHERE `inventory_orders`.`barcode`=`inventory_products`.`barcode` AND `inventory_orders`.active=1) AS INV_ORDER_OUT"                        => 'Inventory Order Canceled',            "(SELECT SUM(`qty_receive`) AS INV_ORDER_RECEIVE FROM `inventory_orders`    WHERE `inventory_orders`.`barcode`=`inventory_products`.`barcode` AND `inventory_orders`.active=1) AS INV_ORDER_RECEIVE"                    => 'Inventory Order Received',                                                "(SELECT SUM(`qty_hold`) - SUM(`qty_release`) AS INVENTORY_ON_HOLD FROM `inventory_holds`   WHERE `inventory_holds`.`barcode`=`inventory_products`.`barcode` AND `inventory_holds`.active=1) AS INVENTORY_ON_HOLD"      => 'Inventory On Hold',                                                               "(SELECT SUM(`qty_order`) - SUM(`qty_cancel`) - SUM(`qty_receive`) AS INVENTORY_ON_ORDER FROM `inventory_orders` WHERE `inventory_orders`.`barcode`=`inventory_products`.`barcode` AND `inventory_orders`.active=1) AS INVENTORY_ON_ORDER" => 'Inventory On Order',                        "(                (SELECT SUM(`inventory_counts`.`qty_in`) - SUM(`inventory_counts`.`qty_out`) AS INVENTORY_TRUE_TMP_1 FROM `inventory_counts` WHERE `inventory_counts`.`barcode`=`inventory_products`.`barcode` AND `inventory_counts`.active=1) -                 (SELECT SUM(`inventory_holds`.`qty_hold`) - SUM(`inventory_holds`.`qty_release`) AS INVENTORY_TRUE_TMP2 FROM `inventory_holds` WHERE `inventory_holds`.`barcode`=`inventory_products`.`barcode` AND `inventory_holds`.active=1) +                  (SELECT SUM(`inventory_orders`.`qty_order`) - SUM(`inventory_orders`.`qty_cancel`) - SUM(`inventory_orders`.`qty_receive`) AS INVENTORY_TRUE_TMP3 FROM `inventory_orders` WHERE `inventory_orders`.`barcode`=`inventory_products`.`barcode` AND `inventory_orders`.active=1)            ) AS INVENTORY_TOTAL" => 'Inventory Future (Pysical-Hold+Order)',                        */                        $this->Join_Array = Array(            //'inventory_counts'  => "LEFT OUTER JOIN `inventory_counts` ON `inventory_counts`.`barcode` = `inventory_products`.`barcode`",            //'inventory_holds'   => "LEFT OUTER JOIN `inventory_holds` ON `inventory_holds`.`barcode` = `inventory_products`.`barcode`",        );        //$this->Group_By         = "`inventory_products`.`barcode`";        $this->Default_Fields   = 'description,barcode,retailer_code,status_retired,group_1,group_2,type_class'; //,INVENTORY_ON_HOLD,INVENTORY_ON_SITE,INVENTORY_ON_ORDER,INVENTORY_TOTAL        $this->Edit_Links_Count = 5;        $this->Unique_Fields    = 'barcode';                $this->Default_Values   = array(            'reorder_point'     => 0,            'status_backorder'  => 0,            'serialized'        => 0,            'barcode'           => $this->Barcode,            'retailer_code'     => $this->Retailer_Code,        );                $this->Use_Selection_Tab    = true;         // (TRUE) false = hide the search tab on the table        $this->Default_List_Size    = 1000;         // how many records to show when table initially loads (higher = slower performance but better user experience)        $this->Close_On_Success     = true;         // (TRUE) true = after editing a recod - close the edit window            } // -------------- END __construct --------------    public function Execute()    {                //$this->InventoryMenu('PRODUCTS');                $this->AddScript();        $this->AddStyle();                                                        $action = Get('action');        switch ($action) {            case 'add':                #$this->Default_Values['barcode'] = $this->Barcode;                $this->AddRecord();            break;                        case 'valuereport':                // ----- getting the VALUE calculation for this barcode                                // ----- Get the build record used for this assembly                $Obj_ValueHandler                           = new Inventory_Valuation_ValueHandler();               // instantitate value class                $Obj_ValueHandler->Barcode                  = $this->Barcode;                                       // pass in the barcode we want value for                $Obj_ValueHandler->Execute();                                                                       // execute the function to get value                $value_array                                = $Obj_ValueHandler->Value_Array;                       // store the value array for report processing                unset($Obj_ValueHandler);                                                                           // memory clean-up                                                // ----- Create a value report based on the calculated value of the build record                $Obj_ValueReport                            = new Inventory_Valuation_ValueSummaryReport();         // instatiate reporting class                $Obj_ValueReport->Barcode                   = $this->Barcode;                                       // set barcode we want report for                $Obj_ValueReport->Value_Array               = $value_array;                                         // set value arraw we want report for                $Obj_ValueReport->Execute();                                                                        // execute the function to make a report                $report                                     = $Obj_ValueReport->Report;                             // store the created report                unset($Obj_ValueReport);                                                                            // memory clean-up                                                // ----- echo out the report to the screen                echo $report;                            break;                        case 'inventoryreport':                AddStylesheet("/css/inventory.css??20121108-1");                $inventory_products_id  = $this->Barcode;                $this->Barcode          = $this->GetInventoryBarcodeFromID($inventory_products_id);                                                $CLASS_EXECUTE_LINK     = 'http://webmanager.whhub.com/office/class_execute';                                $id_next = $inventory_products_id + 1;                $id_prev = $inventory_products_id - 1;                                $eq_next            = EncryptQuery("class=Inventory_InventoryProducts;v1={$id_next};");                $link_next          = $CLASS_EXECUTE_LINK . '?eq=' . $eq_next . '&action=inventoryreport';                                $eq_prev            = EncryptQuery("class=Inventory_InventoryProducts;v1={$id_prev};");                $link_prev          = $CLASS_EXECUTE_LINK . '?eq=' . $eq_prev . '&action=inventoryreport';                                echo "                <div style='border-bottom:1px solid #ddd; margin-bottom:10px; padding-bottom:10px;'>                    <a href='{$link_prev}' >Prev</a>                    &nbsp;&nbsp; | &nbsp;&nbsp;                    <a href='{$link_next}' >Next</a>                </div>                ";                                if ($this->Barcode) {                                        if ($this->StatusInventoryItemExist($this->Barcode)) {                                            $date = Get('date');                                                echo "<br />";                        echo "<div style='color:#000; font-size:18px; font-weight:bold;'>INVENTORY REPORT</div>";                                                if ($date) {                            echo "<div style='color:blue; font-size:14px; font-weight:bold;'>Date Limit: {$date}</div>";                        }                                                echo "<br />";                        echo $this->GetInventoryInOutPriceReport($this->Barcode, $date);                                        } else {                        echo "<h1>INVENTORY ITEM NOT ACTIVE</h1>";                    }                                    } else {                    echo "<h1>BARCODE ERROR</h1>";                }            break;                        case 'list':            default:                #echo "<div style='border:1px dashed red; padding:5px;'>Note: Turn on 'COGS' to see the inventory value.</div>";                #echo "<div style='border:1px dashed red; padding:5px;'>Note: Turn on 'BACKORDER' to get barcode report.</div>";                $this->ListTable();            break;        }    }        public function ExecuteAjax()    {        $QDATA = GetEncryptQuery('eq');        $action = Get('action');                #$_GET['show'] = true;                if (Get('show')) {            echo "<br />QDATA = " . ArrayToStr($QDATA);            echo "<br />action = $action";        }                $return = 0;                switch ($action) {            case 'autocomplete_assembly_lookup':                                // query database for records                $query = Get('term');                $records = $this->SQL->GetArrayAll(array(                    'table' => 'inventory_assemblies',                    'keys'  => 'inventory_assemblies_id, assembly_name, barcode',                    'where' => "(assembly_name LIKE '%{$query}%' OR barcode LIKE '%{$query}%') AND active=1",                ));                                // for records into array format for JSON                $arr = array();                foreach ($records as $record) {                    $arr[] = array(                        'label'             => "{$record['assembly_name']} - {$record['barcode']}",                        'description'       => $record['assembly_name'],                        'id'                => $record['inventory_assemblies_id'],                    );                }                                // convert to JSON format                echo json_encode($arr);     // echo out in JSON form                 $return = '';               // clear return value or it will output and screw up return            break;                        case 'backorder':                $result = $this->ProductBackorderStatusChange($QDATA['id'], 1);                $return = ($result) ? 1 : 0;            break;                        case 'instock':                $result = $this->ProductBackorderStatusChange($QDATA['id'], 0);                $return = ($result) ? 1 : 0;            break;                        case 'reverse':                $result = $this->ProductBackorderStatusReverse($QDATA['id']);                $return = $result;            break;                        case 'nextbarcode':                // get the next unique barcode in the system (starting with a 3)                                do {                    $count  = $this->SQL->Count('inventory_products', 'active=0 OR active=1');                                        $max_length     = 5;                    // how long should barcode be                    $lead           = 30000;                // what should first lead character be                    $value          = $count + 1;           // value of main part of number                    $count_length   = strlen($value);       // get number length                                        // ----- add middle gap 0's                    if ($count_length < $max_length) {                        $value = str_pad($value, ($max_length - 1), 0, STR_PAD_LEFT);                    }                                        // ----- tack on the leading number                    $value  = $lead + $value;               // form up the final number                    $lead   = $lead + 10000;                // increment lead number (for next loop)                                                            if ($lead > 9) {                        $return = "ERROR :: Max Inventory Count";                    }                                        // ----- verify its unique                    $unique = $this->SQL->IsUnique('inventory_products', 'barcode', $value);                                    } while (!$unique);                                $return = $value;                            break;        }                echo $return;    }        public function ProductBackorderStatusReverse($ID)    {        $record = $this->SQL->GetRecord(array(            'table' => $this->Table_Inventory_Products,            'keys'  => "status_backorder",            'where' => "`inventory_products_id`=$ID AND `active`=1",        ));        if ($this->Show_Query) echo '<br /><br />' . $this->SQL->Db_Last_Query;                $newval = ($record['status_backorder'] == 0) ? 1 : 0;            $result = $this->SQL->UpdateRecord(array(            'table'         => $this->Table_Inventory_Products,            'key_values'    => "`status_backorder`=$newval",            'where'         => "`inventory_products_id`=$ID AND `active`=1",        ));        if ($this->Show_Query) echo '<br /><br />' . $this->SQL->Db_Last_Query;                $return_val = ($result) ? $newval : 666;                return $return_val;    }        public function ProductBackorderStatusChange($ID, $BACKORDER_VAL)    {        $result = $this->SQL->UpdateRecord(array(            'table'         => $this->Table_Inventory_Products,            'key_values'    => "`status_backorder`=$BACKORDER_VAL",            'where'         => "`inventory_products_id`=$ID AND `active`=1",        ));        if ($this->Show_Query) echo '<br /><br />' . $this->SQL->Db_Last_Query;        return $result;    }        public function AddStyle()    {        AddStyle("            .cell_error {                /*background-color:#990000;*/                color:#990000;            }        ");    }        public function AddScript()    {        global $CLASS_EXECUTE_LINK_AJAX;        $link = $CLASS_EXECUTE_LINK_AJAX . "?class=Inventory_InventoryProducts";                $SCRIPT = <<<SCRIPT            function tableBackorderClick(idx, value, eq, action)            {                var idbase      = 'TABLE_ROW_ID' + idx + '_';                var idbasecell  = 'TABLE_CELL_ID' + idx + '_';                var cell_bo     = '_STATUS_BACKORDER';                var cell_chg    = '_STATUS_CHANGE';                                var rowNumber = $('#' + idbase + value + ' td:first-child').html().replace('.', '');                $('#' + idbase + value +' td').css('background-color','#ff7');                                switch (action) {                    case 'instock':                        // MARK THIS ITEM AS IN STOCK                        $.get( '{$link};action=instock;eq=' + eq, '', function(data){                            switch (data) {                                case '0':                                    alert('Error: Unable to mark item as in stock');                                break;                                case '1':                                    $('#' + idbase + value +' td').fadeOut('slow', function() {                                        $('#' + idbasecell + value + cell_bo).html('').removeClass('cell_error');                                        $('#' + idbasecell + value + cell_chg).html('{$this->Value_Instock}');                                    }).fadeIn();                                break;                            }                        });                    break;                                        case 'backorder':                        // MARK THIS ITEM AS BACK ORDERED                        $.get( '{$link};action=backorder;eq=' + eq, '', function(data){                            switch (data) {                                case '0':                                    alert('Error: Unable to mark item as backordered');                                break;                                case '1':                                    $('#' + idbase + value +' td').fadeOut('slow', function() {                                        $('#' + idbasecell + value + cell_bo).html('YES').addClass('cell_error');                                        $('#' + idbasecell + value + cell_chg).html('{$this->Value_Backorder}');                                    }).fadeIn();                                break;                            }                        });                    break;                                        case 'reverse':                        // REVERSE STATUS OF THIS ITEM                                                $('#' + idbase + value +' td').css('background-color','#ff7');                                                $.get( '{$link};action=reverse;eq=' + eq, '', function(data){                            switch (data) {                                case '666':                                    alert('Error: Unable to mark item as backordered');                                break;                                case '1':                                    // product IS on backorder                                    $('#' + idbase + value +' td').fadeOut('slow', function() {                                        $('#' + idbasecell + value + cell_bo).html('YES').addClass('cell_error');                                        $('#' + idbasecell + value + cell_chg + ' a').text('{$this->Value_Instock}');                                    }).fadeIn();                                break;                                case '0':                                    // product is NOT backordered                                    $('#' + idbase + value +' td').fadeOut('slow', function() {                                        $('#' + idbasecell + value + cell_bo).html('').removeClass('cell_error');                                        $('#' + idbasecell + value + cell_chg + ' a').text('{$this->Value_Backorder}');                                    }).fadeIn();                                break;                            }                        });                    break;                }                                //$('#' + idbase + value +' td').css('background-color','');                return false;            }                                    function tableEditClick(idx, value, eq, title)            {                var idbase = 'TABLE_ROW_ID' + idx + '_';                $('#' + idbase + value +' td').css('background-color','#ff7');                top.parent.appformCreate('Edit Record - ' + title, '/office/edit_record;eq=' + eq, defaultAppId);                return false;            }            function tableAddClick(eq, title)            {                top.parent.appformCreate('Add Record-' + title, '/office/add_record;eq=' + eq, defaultAppId);                return false;            }SCRIPT;        AddScript($SCRIPT);    }        public function AddScriptGetBarcode()    {        $CLASS_EXECUTE_LINK_AJAX    = '/office/AJAX/class_execute';        $eq                         = EncryptQuery("class=Inventory_InventoryProducts;");        $link                       = $CLASS_EXECUTE_LINK_AJAX . '?eq=' . $eq;                $script = <<<SCRIPT            function getUniqueBarcode(returnID)            {                // CALL THIS CLASS VIA AJAX TO GET BARCODE                $.get( '{$link};action=nextbarcode', '', function(data){                    $('#' + returnID).val(data);                });            }SCRIPT;        AddScript($script);    }        public function ProcessTableCell($field, &$value, &$td_options, $id='')    {        # ============ WHEN VIEWING A TABLE OF ALL RECORDS ============                parent::ProcessTableCell($field, $value, $td_options, $id);        switch ($field) {                        case 'INVENTORY_PHYSICAL':            case 'INVENTORY_TOTAL':                $value      = ($value == '' || $value == 0) ? "0" : $value;            break;                        case 'COGS':                $barcode    = $this->GetInventoryBarcodeFromID($id);                $cogs       = $this->CalculateInventoryValue($barcode);                                $CLASS_EXECUTE_LINK     = '/office/class_execute';                $eq3                    = EncryptQuery("class=Inventory_InventoryProducts;v1={$id};");                $link3                  = $CLASS_EXECUTE_LINK . '?eq=' . $eq3 . '&action=inventoryreport';                $script3                = "top.parent.appformCreate('Window', '{$link3}', 'apps'); return false;";                $report                 = "<a href='#' class='row_viewline' title='COGS Report'   onclick=\"{$script3}; return false;\">?</a>";                                $value = "                    <table width='100%'>                    <tr><td width='100%'>{$cogs}</td><td>{$report}</td></tr>                    </table>                ";            break;                                    case 'part_cost':            case 'QUICK_VALUE':                $value      = money_format("%n", $value);            break;                        case 'status_retired':                $value          = ($value == 1) ? 'retired' : '';                //$td_options     = ($value == 1) ? " bgcolor='red' " : '';                            break;                        case 'status_use_assembly_pricing':                $value          = ($value == 1) ? 'YES' : '';            break;                        case 'SINGLE_COGS':                $barcode    = $this->GetInventoryBarcodeFromID($id);                $cogs       = $this->InventoryItemLastCost($barcode);                $value      = money_format("%n", $cogs);            break;                        default:                                $CLASS_EXECUTE_LINK     = '/office/class_execute';                                                // ----- create print link                 $barcode                = $this->GetInventoryBarcodeFromID($id);                $sku                    = $this->GetInventoryItemRetailerCodeFromBarcode($barcode);                $description            = $this->GetInventoryItemDescriptionFromBarcode($barcode);                $eq_print               = EncryptQuery("class=Dymo_PrintInventoryLabel;v1={$barcode};v2={$sku};v3={$description};");                $link_print             = $CLASS_EXECUTE_LINK . '?eq=' . $eq_print . '&action=';                $script_print           = "top.parent.appformCreate('Window', '{$link_print}', 'apps'); return false;";                                                // ----- create value report link                $eq_value               = EncryptQuery("class={$this->Classname};v1={$barcode};");                $link_value             = $CLASS_EXECUTE_LINK . '?eq=' . $eq_value . '&action=valuereport';                $script_value           = "top.parent.appformCreate('Window', '{$link_value}', 'apps'); return false;";                                // ----- output the action buttons                $col_view   = ($this->Flags['view'] == 'false')   ? ''    : "<a href=`#` class=`row_view`         title=`View`            onclick=`tableViewClick('@IDX@','@VALUE@','@EQ@', '@TITLE@'); return false;`></a>";                $col_edit   = ($this->Flags['edit'] == 'false')   ? ''    : "<a href=`#` class=`row_edit`         title=`Edit`            onclick=`tableEditClick('@IDX@','@VALUE@','@EQ@', '@TITLE@'); return false;`></a>";                $col_delete = ($this->Flags['delete'] == 'false') ? ''    : "<a href=`#` class=`row_delete`       title=`Delete`          onclick=`tableDeleteClick('@IDX@','@VALUE@','@EQ@'); return false; `></a>";                $col_value  = ($this->Flags['value'] == 'false')  ? ''    : "<a href=`#` class=`row_dollarsign`   title=`Value Report`    onclick=`{$script_value}; return false;`></a>";                $col_print  = ($this->Flags['print'] == 'false')  ? ''    : "<a href=`#` class=`row_print`        title=`Print Label`     onclick=`{$script_print}; return false;`></a>";                                $this->Edit_Links = qqn("                    <td align=`center`>{$col_view}</td>                    <td align=`center`>{$col_edit}</td>                    <td align=`center`>{$col_delete}</td>                    <td align=`center`>{$col_value}</td>                    <td align=`center`>{$col_print}</td>                    ");            break;        }    }                private function JavascriptCreateTable()    {        $this->ScriptJSONTableGeneric();                                    // Add in the generic code held in the BaseClass        $eq = EncryptQuery("class={$this->Classname}");       // Class the autocomplete should call (ideally this class)                $script = <<<SCRIPT                        // ----- autocomplete -----            var termTemplate = "<span class='ui-autocomplete-term'>%s</span>";                        $('#FORM_temp_10').autocomplete({                                 source          : 'http://webmanager.whhub.com/office/AJAX/class_execute.php;eq={$eq};action=autocomplete_assembly_lookup',                minChars        : 0,    // how many characters tot ype before starting function call                selectFirst     : true,     // allows tab to select the top option in returned values                autoFocus       : true,                                // where do we stick the returned results                select: function( event, ui ) {                    $( "#FORM_temp_10" ).val( ui.item.label );                    $( "#FORM_inventory_assemblies_id" ).val( ui.item.id );                 // store Barcode                                        return false;                },                                // format the matched text in search terms                // NOTE/BUG :: Currently case sensitive so doesn't highlight non-case-matching results                open: function(e,ui) {                    var                        acData = $(this).data('autocomplete'),                        styledTerm = termTemplate.replace('%s', acData.term);                    acData                        .menu                        .element                        .find('a')                        .each(function() {                            var me = $(this);                            me.html( me.text().replace(acData.term, styledTerm) );                        });                }            })                        // format the display of the data in the autocomplete (should probably be a custom function on this class)            // (everything else should be in the base class)            .data( "autocomplete" )._renderItem = function( ul, item ) {                return $( "<li>" )                    .data( "item.autocomplete", item )                    .append( "<a>" + item.label + "<br>"  + "</a>" )                    .appendTo( ul );            };            SCRIPT;        AddScriptOnReady($script);    }        public function SetFormArrays()    {        // ----- required CSS file        AddStylesheet("/css/inventory.css");                                // ----- fill in the assembly information        $this->Inventory_Assemblies_Id          = (Post('FORM_inventory_assemblies_id')) ? $_POST['FORM_inventory_assemblies_id'] : 0;        $assembly_details                       = $this->GetAssemblyDetails($this->Inventory_Assemblies_Id);        $assembly_display                       = ($this->Inventory_Assemblies_Id) ? "display:none;" : '';                        // ----- required javascript functions        $this->JavascriptDatepickerFunctionality(array('FORM_price_reference_date'));       // date-picker functionality        $this->JavascriptToggleFunctionality();                                             // toggling show/hide a div area        $this->AddScriptGetBarcode();                                                       // Add javascript for getting unique barcode        $this->JavascriptCreateTable();             // Javascript for creating table from array of data                        // ----- create lists from existing database fields        $vendors                = $this->SQL->GetFieldValues($this->Table, 'vendor', "vendor != ''");        $categories             = $this->SQL->GetFieldValues($this->Table, 'report_category', "report_category != ''");        $vendor_list            = Form_ArrayToList(