='', $where='', $order='', $start_list='', $list_size='', $joins='', $get_count=true)    {        if (is_array($table)) extract($table, EXTR_OVERWRITE);        // joins need to be provide in full SQL        if ($keys == '') {            return '';        }        $start_list = intOnly($start_list);        $list_size  = intOnly($list_size);        if (empty($order) or ($order == 'none')) {            $order = '';        } else {            $order = "ORDER BY $order";        }        if (!empty($where)) {            $where = "WHERE $where";        }        $table = $this->QuoteTables($table);        // --------- get row count ---------        if ($get_count) {            $query = "SELECT COUNT(*) FROM $table $joins $where";            $query_result = $this->Query('GetArray Count', $query, PDO::FETCH_NUM);            if ($query_result) {                $this->Number_Rows = $query_result->fetchColumn();                $query_result->closeCursor();                $query_result = null;                if ($start_list < 0) $start_list = $this->Number_Rows + $start_list;  // gets last rows (-100, would get last 100 rows)            } else {                $this->Number_Rows = 0;            }        } else {            $this->Number_Rows = 0;        }        if ((empty($start_list)) and (empty($list_size))) {            $LIMIT = '';        } else {            $LIMIT = "LIMIT $start_list, $list_size";        }        $query = "SELECT $keys FROM $table $joins $where $this->Group $order $LIMIT";        $query_result  = $this->Query('GetArray', $query);        $RESULT = array();        if ($query_result) {            $RESULT = $query_result->fetchAll();            $query_result->closeCursor();        }        return $RESULT;    }        private function GetAssemblyDetails($ASSEMBLY_ID)    {        $assembly_details = "";                if ($ASSEMBLY_ID) {                        $record = $this->SQL->GetRecord(array(                'table' => 'inventory_assemblies',                'keys'  => 'assembly_name, inventory_assemblies.barcode, inventory_products.description, inventory_products.retailer_code',                'where' => "`inventory_assemblies`.`inventory_assemblies_id`='{$ASSEMBLY_ID}'",                'joins' => "LEFT JOIN `inventory_products` ON `inventory_products`.`barcode` = `inventory_assemblies`.`barcode`"            ));            $this->EchoQuery();                        if ($record) {                //$cogs = $this->CalculateAssemblyValue($ASSEMBLY_ID);                $cogs = 'n/a';                $assembly_details = "                    <div style='background-color:#fff; padding:5px; font-size:14px;'>ASSEMBLY DETAILS</div>                    <div style='padding:5px; font-size:12px;'>                        <b>APDM SKU:</b> {$record['retailer_code']}<br />                        <b>Barcode:</b> {$record['barcode']}<br />                        <b>Description:</b> {$record['description']}<br />                        <b>COGS:</b> {$cogs}                    </div>                    ";                                    $_POST['FORM_temp_10']           = "{$record['assembly_name']} - {$record['barcode']}";            } else {                echo "ERROR :: UNABLE TO LOAD ASSEMBLY";                exit();            }                    }                return $assembly_details;    }                        public function ViewRecord($id, $field_list='', $field='')    {        # FUNCTION :: function to view a record                        // ----- get original record from database -----        $record = $this->SQL->GetRecord(array(            'table' => $this->Table,            'keys'  => "*",            'where' => "{$this->Index_Name}={$id}",        ));        $this->EchoQuery();                        // ----- add styles -----        AddStylesheet("/css/inventory.css");        AddStyle("            .vr_header {                font-size:          22px;                font-weight:        bold;                color:              #003399;            }            .vr_header_barcode {                font-size:          16px;                font-weight:        bold;                color:              #003399;            }                        .vr_table td:first-child {                font-weight:        bold;                text-align:         right;            }                        .vr_assy_table {                border:             1px solid blue;                border-collapse:    collapse;                            }            .vr_assy_table th {                border:             1px solid black;                font-weight:        bold;                background-color:   #C2D1F0;                padding:            3px;            }            .vr_assy_table td {                border:             1px solid black;                padding:            3px;            }        ");                        // ----- format the various output areas -----        $default_cost       = money_format("%n", $record['part_cost']);        $assembly           = '';                        // ----- get the assembly details -----        $Obj_Explode    = new Inventory_AssemblyExplode();        $is_assembly    = $Obj_Explode->CheckIfSubAssembly($record['barcode']);                $cogs = 'Not Calculated';        if (!$is_assembly) {            $Obj_COGS               = new Inventory_Valuation_SingleItemCostCalculation();            $Obj_COGS->Barcode      = $record['barcode'];            $Obj_COGS->Method       = 'total_average';            $Obj_COGS->Execute();            $cost_each              = $Obj_COGS->Price_Calculated_Each;            $cogs                   = money_format("%n", $cost_each);        }                if ($is_assembly) {            // ----- get the BOM for this assembly            $Obj_Explode->Barcode = $record['barcode'];            $Obj_Explode->Execute();                                    # ========== Inventory_InventoryAssemblyCalculateValue ==========            // ----- over-ride the stored default cost - cause this is an assembly            #$Obj_COGS               = new Inventory_InventoryAssemblyCalculateValue();            #$Obj_COGS->Barcode      = $record['barcode'];            #$cogs_total             = $Obj_COGS->Execute();            #$cogs_arr               = $Obj_COGS->Cogs_Array;            #$cogs_next              = $cogs_arr[0]['cogs_total'];            #$default_cost           = "<span style='font-size:14px;'>(see ASSEMBLY DETAILS)</span>"; //money_format("%n", $cogs_next);                                    $Obj_COGS               = new Inventory_Valuation_SingleItemCostCalculation();            $Obj_COGS->Barcode      = $record['barcode'];            $Obj_COGS->Method       = 'total_average';            $Obj_COGS->Execute();            $cost_each              = $Obj_COGS->Price_Calculated_Each;            $default_cost           = "<span style='font-size:14px;'>(see ASSEMBLY DETAILS) ===> {$cost_each}</span>"; //money_format("%n", $cogs_next);                                    $cogs_total = 0;;            $table_rows = '';            foreach ($Obj_Explode->BOM as $barcode => $quantity) {                $sku                = $this->GetInventoryItemRetailerCodeFromBarcode($barcode);                //$cogs               = 1.11;                //$cogs               = $this->InventoryItemLastCost($barcode);                $cogs = 0;                                $total              = ($quantity * $cogs);                $cogs_formatted     = money_format("%n", $cogs);                $total_formatted    = money_format("%n", $total);                $cogs_total        += $total;                                                $table_rows .= "<tr><td>{$barcode}</td><td>{$sku}</td><td align='center'>{$quantity}</td><td align='center'>{$cogs_formatted}</td><td align='center'>{$total_formatted}</td></tr>";                //$table_rows .= "<tr><td>{$barcode}</td><td>{$sku}</td><td align='center'>{$quantity}</td></tr>";            }                        $cogs_total = money_format("%n", $cogs_total);            $assy_table = "                <table class='vr_assy_table' width='100%'>                <tr><th>BARCODE</th><th>SKU</th><th>QTY</th><th>COGS</th><th>TOTAL</th></tr>                {$table_rows}                <tr><th colspan='4' align='right'>TOTAL:</th><th>{$cogs_total}</th></tr>                </table>";                                //<tr><th>BARCODE</th><th>SKU</th><th>QTY</th></tr>                //<tr><th>BARCODE</th><th>SKU</th><th>QTY</th><th>COGS</th><th>TOTAL</th></tr>                //<tr><th colspan='4' align='right'>TOTAL:</th><th>{$cogs_total}</th></tr>                            $assembly = "                <div class='shadow form_section_wrapper'>                    <div class='form_section_header'>ASSEMBLY DETAILS (ID: {$record['inventory_assemblies_id']})</div>                    <br />                    <div>{$assy_table}</div>                </div>                <br /><br />";                        }                        $vendor = "        <div class='shadow form_section_wrapper'>            <div class='form_section_header'>VENDOR DETAILS</div>            <table class='vr_table'>                <tr><td>Preferred Vendor:</td><td>{$record['vendor']}</td></tr>                <tr><td>Manufacturer Code:</td><td>{$record['manufacturer_code']}</td></tr>            </table>        </div>        <br /><br />";        $vendor = ($record['vendor'] || $record['manufacturer_code']) ? $vendor : '';                        $price_reference = "            <div class='shadow form_section_wrapper'>            <div class='form_section_header'>DEFAULT PRICE REFERENCE</div>                <table class='vr_table'>                <tr><td>Reference Source Date:</td><td>{$record['price_reference_date']}</td></tr>                <tr><td>Reference Price:</td><td>{$record['price_reference_price']}</td></tr>                <tr><td>Reference Quantity:</td><td>{$record['price_reference_quantity']}</td></tr>                <tr><td>Reference Source:</td><td>{$record['price_reference_source']}</td></tr>                <tr><td>Reference Source Number:</td><td>{$record['price_reference_number']}</td></tr>                <tr><td>Reference Source URL:</td><td>{$record['price_reference_url']}</td></tr>                <tr><td>Reference Notes:</td><td>{$record['price_reference_notes']}</td></tr>                </table>            </div>            <br /><br />";                        $general = "            <div class='shadow form_section_wrapper'>                <div class='form_section_header'>GENERAL INFORMATION</div>                <table class='vr_table'>                    <tr><td>Description:</td><td>{$record['description']}</td></tr>                    <tr><td>Notes:</td><td>{$record['notes']}</td></tr>                    <tr><td>Reorder Point:</td><td>{$record['reorder_point']}</td></tr>                    <tr><td>Leadtime:</td><td>{$record['leadtime_days']}</td></tr>                    <tr><td>Last Inventory Check:</td><td>{$record['date_last_checked']}</td></tr>                </table>            </div>            <br /><br />";                                            // ----- output the content -----        $output = "            <div class='vr_header'>{$record['retailer_code']}</div>            <div class='vr_header_barcode'>{$record['barcode']}</div>            <br /><br />            <div class='vr_header'>COGS: {$cogs}</div>            <div class='__vr_header_barcode'>Default Cost: {$default_cost}</div>            <br /><br />                        {$general}            {$vendor}            {$assembly}            {$price_reference}            ";        echo $output;            }    }  // -------------- END CLASS --------------